#include <zmq.h>
#include <stdio.h>
#include <unistd.h>
#include <string.h>
#include <assert.h>

#define NUMBER_OF_MESSAGES 10000
#define LENGTH_OF_MESSAGES 1000

int main (void)
{
    void *context = zmq_ctx_new ();
    void *responder = zmq_socket (context, ZMQ_REP);
    int rc = zmq_bind (responder, "tcp://*:5555");
    assert (rc == 0);

    for(int j=0;j<NUMBER_OF_MESSAGES;j++)
    {
        
        char Message[LENGTH_OF_MESSAGES];
        if(zmq_recv(responder,Message,1000,0)==1000)
        {
		zmq_send (responder, "", 1000, 0);
	}
	else {printf("error"); break;}
    }
    
    zmq_close (responder);
    zmq_ctx_destroy (context);
    return 0;
}
