//  Hello World client
#include <zmq.h>
#include <string.h>
#include <stdio.h>
#include <unistd.h>
#include <sys/time.h>
#include <assert.h>



int main (void)
{
    FILE *fp;
    int j;
    struct timeval tve;
    void *context = zmq_ctx_new ();
    void *requester = zmq_socket (context, ZMQ_PUB);
    int rc = zmq_bind (requester, "tcp://*:5555");
    assert (rc == 0);

    sleep(20);
    
    /*
    //send message and record time in a file
    fp=fopen("pub","w");
    for(j=0;j<10000;j++)
    {
        zmq_send (requester, "", 1000, 0);
        gettimeofday(&tve,NULL);
        fprintf(fp,"%ld  %ld\n",tve.tv_sec,tve.tv_usec);
    }
    
    fclose(fp);
    */
    
    //to see which messages are received by sub
    j=0;
    while(1){
		char k[1000];
		sprintf(k,"%d",j);
		j++;
		zmq_send (requester, k, 1000, 0);
    }
	

    zmq_close (requester);
    zmq_ctx_destroy (context);
    return 0;
}
